(30, 'Minimul elementelor fiecarei coloane a unei matrici', '2015-03-31', 'Vladutu Loredana', '', 'minim, matrice, fiecare', '<p>\r\n#include&#60;iostream&#62; <br />\r\nusing&nbsp;namespace&nbsp;std;<br />\r\nint&nbsp;main(&nbsp;)<br />\r\n{<img src="images/$img2" style="float:right;"><br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;int&nbsp;A[100][100],&nbsp;n&nbsp;,&nbsp;m,&nbsp;i,&nbsp;j;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;cout&#60;&#60;"Numarul&nbsp;liniilor=";&nbsp;cin&#62;&#62;m;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;cout&#60;&#60;"Numarul&nbsp;coloanelor=";&nbsp;cin&#62;&#62;n;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;//citirea&nbsp;elementelor&nbsp;matricei<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;for(i=0;i&#60;m;i++)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for(j=0;j&#60;n;j++)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;cout&#60;&#60;"A["&#60;&#60;i&#60;&#60;"]["&#60;&#60;j&#60;&#60;"]=";<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;cin&#62;&#62;A[i][j];<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;cout&#60;&#60;"Elementele&nbsp;matricei&nbsp;sunt:"&#60;&#60;endl;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;for(i=0;i&#60;m;i++)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;{<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for(j=0;j&#60;n;j++)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;cout&#60;&#60;A[i][j]&#60;&#60;"&nbsp;";<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;cout&#60;&#60;endl;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;}<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;int&nbsp;maxim,&nbsp;minim;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;for(j=0;j&#60;n;j++)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;{<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;minim=A[0][j];<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for(i=0;i&#60;m;i++)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(A[i][j]&#60;minim)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;minim=A[i][j];<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;cout&#60;&#60;"Minimul&nbsp;coloanei&nbsp;"&#60;&#60;(j+1)&#60;&#60;"="&#60;&#60;minim&#60;&#60;endl;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;}<br />\r\n}\r\n\r\n</p>', '', 'trebuie545.jpg', '', '', '', '', '', '', '', '', '', '', '', '', '', 'Matrici', 'Operatii cu matrici', '', '', '0', '0', '', '', '', '', '', ''),
(31, 'Problema 1', '2015-04-20', 'Vladutu Loredana', 'pr115.jpg', 'problema 1', '<h3>\r\nSe citeste numarul natural <b>n</b>. Sa se construiasca matricea patratica astfel:\r\n<ul>\r\n<li>pe diagonala principala se va gasi numai numarul 0 ; </li>\r\n<li>sub diagonala principala se va gasi numai numarul 1 ; </li>\r\n<li>deasupra diagonalei principale se va gasi numai numarul 2 . </li>\r\n</ul>\r\n</h3>\r\n<p>#include&#60;iostream&#62; <br />\r\nusing&nbsp;namespace&nbsp;std;<br />\r\nint&nbsp;main(&nbsp;)<br />\r\n{<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;int&nbsp;A[100][100],&nbsp;n,&nbsp;i,&nbsp;j ;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;cout&#60;&#60;"Numarul&nbsp;liniilor&nbsp;si&nbsp;numarul&nbsp;coloanelor=";&nbsp;cin&#62;&#62;n;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;for(i=0;i&#60;n;i++)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for(j=0;j&#60;n;j++)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(i==j)&nbsp;A[i][j]=0;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;else&nbsp;if(i&#60;j)&nbsp;A[i][j]=2;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;else&nbsp;A[i][j]=1;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;for(i=0;i&#60;n;i++)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;{<img src="images/$img1" style="float:right;"><br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for(j=0;j&#60;n;j++)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;cout&#60;&#60;A[i][j]&#60;&#60;"&nbsp;";<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;cout&#60;&#60;endl;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;}<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;0;<br />\r\n}\r\n</p>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', 'Matrici', 'Matrici patratice', '', '', '0', '0', '', '', '', '', '', ''),
(32, 'Problema 2', '2015-04-20', 'Vladutu Loredana', 'pr266.jpg', 'problema 2', '<p>\r\n<h3>\r\nSe citeste numarul natural <b>n</b>. Sa se construiasca matricea de caracte A astfel:\r\n<ul>\r\n<li>pe diagonala secundara sa se gaseasca numai litera A ; </li>\r\n<li>deasupra diagonalei secundare sa se gaseasca numai litera Q ; </li>\r\n<li>sub diagonala secundara sa se gaseasca numai litera T.  </li>\r\n</ul>\r\n</h3>\r\n#include&#60;iostream&#62; <br />\r\nusing&nbsp;namespace&nbsp;std; <br />\r\nint&nbsp;main(&nbsp;)<br />\r\n{<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;char&nbsp;A[100][100];<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;int&nbsp;n,&nbsp;i,&nbsp;j ;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;cout&#60;&#60;"Numarul&nbsp;liniilor&nbsp;si&nbsp;numarul&nbsp;coloanelor=";&nbsp;cin&#62;&#62;n;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;for(i=0;i&#60;n;i++)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for(j=0;j&#60;n;j++)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(i+j==n-1)&nbsp;A[i][j]=&#39;A&#39;;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;else&nbsp;if(i+j&#60;n-1)&nbsp;A[i][j]=&#39;Q&#39;;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;else&nbsp;A[i][j]=&#39;T&#39;;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;for(i=0;i&#60;n;i++)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;{<img src="images/$img1" style="float:right;"><br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for(j=0;j&#60;n;j++)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;cout&#60;&#60;A[i][j]&#60;&#60;"&nbsp;";<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;cout&#60;&#60;endl;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;}<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;0;<br />\r\n}\r\n</p>\r\n', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', 'Matrici', 'Matrici patratice', '', '', '0', '0', '', '', '', '', '', ''),
(33, 'Problema 3', '2015-04-20', 'Vladutu Loredana', 'poza159.jpg', 'problema 3', '\r\n<h3>Verificati daca matricea este simetrica fata de diagonala principala.</h3>\r\n\r\n<p>#include&#60;iostream&#62; <br />\r\nusing&nbsp;namespace&nbsp;std;<br />\r\nint&nbsp;main(&nbsp;)<br />\r\n{<img src="images/$img1" style="float:right;"><br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;int&nbsp;A[100][100],&nbsp;n,&nbsp;i,&nbsp;j,&nbsp;ok;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;cout&#60;&#60;"Numarul&nbsp;liniilor&nbsp;si&nbsp;numarul&nbsp;coloanelor=";&nbsp;cin&#62;&#62;n;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;//citirea&nbsp;elementelor&nbsp;matricei<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;for(i=0;i&#60;n;i++)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for(j=0;j&#60;n;j++)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;cout&#60;&#60;"A["&#60;&#60;i&#60;&#60;"]["&#60;&#60;j&#60;&#60;"]=";<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;cin&#62;&#62;A[i][j];<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;cout&#60;&#60;"Elementele&nbsp;matricei&nbsp;sunt:"&#60;&#60;endl;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;for(i=0;i&#60;n;i++)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;{<img src="images/$img2" style="float:right;"><br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for(j=0;j&#60;n;j++)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;cout&#60;&#60;A[i][j]&#60;&#60;"&nbsp;";<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;cout&#60;&#60;endl;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;}<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;ok=1;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;for(i=0;i&#60;=n-2&&ok;i++)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for(j=i+1;j&#60;=n-1&&ok;j++)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(A[i][j]!=A[j][i])<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ok=0;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;if(ok)&nbsp;cout&#60;&#60;"Da";<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;else&nbsp;cout&#60;&#60;"Nu";<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;0;<br />\r\n}\r\n</p>', '', 'poza251.jpg', '', '', '', '', '', '', '', '', '', '', '', '', '', 'Matrici', 'Matrici patratice', '', '', '0', '0', '', '', '', '', '', ''),
(34, 'Problema 4', '2015-04-20', 'Vladutu Loredana', 'img17.jpg', 'problema 4', '<p>\r\n<h3>Verificati daca matricea este simetrica fata de diagonala secundara.</h3>\r\n#include&#60;iostream&#62; <br />\r\nusing&nbsp;namespace&nbsp;std; <br />\r\nint&nbsp;main(&nbsp;)<br />\r\n{<img src="images/$img1" style="float:right;"><br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;int&nbsp;A[100][100],&nbsp;n,&nbsp;i,&nbsp;j,&nbsp;ok;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;cout&#60;&#60;"Numarul&nbsp;liniilor&nbsp;si&nbsp;numarul&nbsp;coloanelor=";&nbsp;cin&#62;&#62;n;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;//citirea&nbsp;elementelor&nbsp;matricei<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;for(i=0;i&#60;n;i++)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for(j=0;j&#60;n;j++)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;cout&#60;&#60;"A["&#60;&#60;i&#60;&#60;"]["&#60;&#60;j&#60;&#60;"]=";<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;cin&#62;&#62;A[i][j];<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;cout&#60;&#60;"Elementele&nbsp;matricei&nbsp;sunt:"&#60;&#60;endl;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;for(i=0;i&#60;n;i++)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;{<img src="images/$img2" style="float:right;"><br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for(j=0;j&#60;n;j++)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;cout&#60;&#60;A[i][j]&#60;&#60;"&nbsp;";<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;cout&#60;&#60;endl;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;}<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;ok=1;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;for(i=0;i&#60;=n-2&&ok;i++)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for(j=0;j&#60;=n-i-2&&ok;j++)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(A[i][j]!=A[n-1-j][n-1-i])<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ok=0;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;if(ok)&nbsp;cout&#60;&#60;"Da";<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;else&nbsp;cout&#60;&#60;"Nu";<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;0;<br />\r\n}\r\n\r\n</p>', '', 'img248.jpg', '', '', '', '', '', '', '', '', '', '', '', '', '', 'Matrici', 'Matrici patratice', '', '', '0', '0', '', '', '', '', '', '');

